// src/redux/store.js
import { createStore, applyMiddleware } from 'redux';
import rootReducer from './reducers'; // Create this file (rootReducer) with your reducers
import promiseMiddleware from 'redux-promise-middleware'; // Install with: npm install redux-promise-middleware

const store = createStore(rootReducer, applyMiddleware(promiseMiddleware()));

export default store;
