// src/redux/reducers.js
const initialState = {
    example: {
      data: null,
    },
    // Add more state slices as needed
  };
  
  const rootReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'EXAMPLE_ACTION':
        return {
          ...state,
          example: {
            ...state.example,
            data: action.payload,
          },
        };
      // Add more cases for other actions as needed
      default:
        return state;
    }
  };
  
  export default rootReducer;
