import  { useEffect, useState } from 'react'
import axios from 'axios'
import Pagination from 'react-bootstrap/Pagination'
import 'bootstrap/dist/css/bootstrap.min.css'
import React from 'react'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'

const Body = () => {
  const [movies, setMovies] = useState([])
  const [currentPage, setCurrentPage] = useState(1)
  const [totalPages, setTotalPages] = useState(1)

  useEffect(() => {
    const apiKey = 'c45a857c193f6302f2b5061c3b85e743'
    const apiUrl = 'https://api.themoviedb.org/3/movie/popular'

    axios
      .get(apiUrl, {
        params: {
          api_key: apiKey,
          language: 'en-US',
          page: currentPage,
        },
      })
      .then((response) => {
        console.log(response.data)
        setMovies(response.data.results)
        setTotalPages(response.data.total_pages)
      })
      .catch((error) => {
        console.error('Error fetching data:', error)
      })
  }, [currentPage])

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page)
    }
  }

  const renderPaginationItems = () => {
    const items = []
    const displayPages = 5

    let startPage = Math.max(1, currentPage - Math.floor(displayPages / 2))
    let endPage = startPage + displayPages - 1

    if (endPage > totalPages) {
      endPage = totalPages
      startPage = Math.max(1, endPage - displayPages + 1)
    }

    if (startPage > 1) {
      items.push(
        <Pagination.First key="first" onClick={() => handlePageChange(1)} />,
        <Pagination.Ellipsis key="ellipsis-start" disabled />,
      )
    }

    for (let i = startPage; i <= endPage; i++) {
      items.push(
        <Pagination.Item
          key={i}
          active={i === currentPage}
          onClick={() => handlePageChange(i)}
        >
          {i}
        </Pagination.Item>,
      )
    }

    if (endPage < totalPages) {
      items.push(
        <Pagination.Ellipsis key="ellipsis-end" disabled />,
        <Pagination.Last
          key="last"
          onClick={() => handlePageChange(totalPages)}
        />,
      )
    }

    return items
  }

  return (
    <div style={{ background: '#282b33' }}>
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          paddingLeft: '20px',
          justifyContent: 'center',
        }}
      >
        {movies.map((movie) => (
          <Link to="/">
            <div
              key={movie.id}
              style={{
                width: '50%',
                maxWidth: '300px',
                margin: '10px',
                //   textAlign: 'center',
              }}
            >
              <img
                src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                alt={movie.title}
                style={{ width: '100%', height: 'auto' }}
              />
              <p style={{ color: 'white' }}>{movie.title}</p>
              <p style={{ color: 'white' }}>Rating: {movie.vote_average}</p>
            </div>
          </Link>
        ))}
        <Pagination>
          <Pagination.Prev onClick={() => handlePageChange(currentPage - 1)} />
          {renderPaginationItems()}
          <Pagination.Next onClick={() => handlePageChange(currentPage + 1)} />
        </Pagination>
      </div>
    </div>
  )
}

export default Body
