// MovieDetails.js
import React, { useEffect, useState } from 'react'
import axios from 'axios'

const MovieDetails = ({ match }) => {
  const [movieDetails, setMovieDetails] = useState(null)
  const [castDetails, setCastDetails] = useState(null)

  useEffect(() => {
    const apiKey = 'c45a857c193f6302f2b5061c3b85e743'
    const movieId = match.params.id
    const apiUrl = `https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}&language=en-US`

    const castapiUrl = `https://api.themoviedb.org/3/movie/${movieId}/credits?api_key=${apiKey}&language=en-US`
    axios
      .get(apiUrl)
      .then((response) => {
        console.log(response.data)
        setMovieDetails(response.data)
      })
      .catch((error) => {
        console.error('Error fetching movie details:', error)
      })

    axios
      .get(castapiUrl)
      .then((response) => {
        console.log(response.data)
      
      })
      .catch((error) => {
        console.error('Error fetching movie details:', error)
      })


  }, [match.params.id])

  if (!movieDetails) {
    return <p>Loading...</p>
  }

  return (
    <div style={{background:"#282b33"}}>
      <div style={{  background: '#282b33',  color: 'white',
            padding: '20px', border:"3px solid white", borderRadius:"5px"}}>
        <div
          style={{
            display: 'flex',
            color: 'white',
            padding: '10px',
          }}
        >
          {/* Image on the left */}
          <img
            src={`https://image.tmdb.org/t/p/w500/${movieDetails.poster_path}`}
            alt={movieDetails.title}
            style={{
              width: '100%',
              maxWidth: '150px',
              height: '250px',
              marginRight: '10px',
            }}
          />

          {/* Movie details on the right */}
          <div>
            <h3>{movieDetails.title}</h3>
            <h6 style={{color:"#94c1f1", marginBottom:"10px"}}>Rating: {movieDetails.vote_average}</h6>
            <p>
              <span style={{ border: '1px solid #324e69', padding: '5px' }}>
                {movieDetails.runtime} mins
              </span>{' '}
              Genres:{' '}
              {movieDetails.genres.map((genre) => genre.name).join(', ')}
            </p>
            <p>Release Date: {movieDetails.release_date}</p>
          </div>

          {/* Overview just below the image */}
        </div>
        <div>
          <h4> Overview: </h4>
          <p>{movieDetails.overview}</p>
        </div>
      </div>

      {/* Cast details */}
      <div style={{color:"white"}}>
          Cast:


      </div>
    </div>
  )
}

export default MovieDetails
