// MovieDetails.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MovieDetails = ({ match }) => {
  const [movieDetails, setMovieDetails] = useState(null);

  useEffect(() => {
    const apiKey = 'c45a857c193f6302f2b5061c3b85e743';
    const movieId = match.params.id;
    const apiUrl = `https://api.themoviedb.org/3/movie/${movieId}?api_key=${apiKey}&language=en-US`;

    axios
      .get(apiUrl)
      .then((response) => {
        console.log(response.data);
        setMovieDetails(response.data);
      })
      .catch((error) => {
        console.error('Error fetching movie details:', error);
      });
  }, [match.params.id]);

  if (!movieDetails) {
    return <p>Loading...</p>;
  }

  // Create the Netflix URL with the movie's ID
  const netflixUrl = `https://www.netflix.com/title/${movieDetails.id}`;

  return (
    <div>
      <h2>{movieDetails.title}</h2>
      <img
        src={`https://image.tmdb.org/t/p/w500/${movieDetails.backdrop_path}`}
        alt={movieDetails.title}
        style={{ width: '100%', height: 'auto' }}
      />
      <p>{movieDetails.overview}</p>
      <p>Release Date: {movieDetails.release_date}</p>
      <a href={netflixUrl} target="_blank" rel="noopener noreferrer">
        Watch on Netflix
      </a>
    </div>
  );
};

export default MovieDetails;
